// Get the temp directory.
var root = Directory("./");
var temp = root + Directory("temp");

// Clean the directory.
CleanDirectory(temp);

var directoriesToDelete = new DirectoryPath[]{
    Directory("be"),
    Directory("gone")
};
DeleteDirectories(directoriesToDelete, recursive:true);

Func<IFileSystemInfo, bool> exclude_node_modules =
fileSystemInfo=>!fileSystemInfo.Path.FullPath.EndsWith(
                "node_modules",
                StringComparison.OrdinalIgnoreCase);
CleanDirectories("./src/**/bin/debug", exclude_node_modules);

member.returns:
true if  refers to an existing directory;
            false if the directory does not exist or an error occurs when trying to
            determine if the specified path exists.

example.code:
// Get the temp file.
var root = Directory("./");
var temp = root + File("temp");

// Delete the file.
CleanDirectory(temp);

member.returns:
true if  refers to an existing file;
            false if the file does not exist or an error occurs when trying to
            determine if the specified file exists.

example.code:
var files = GetFiles("./**/Cake.*.dll");
foreach(var file in files)
{
    Information("File: {0}", file);
}

